class MyCalculator:
    def add_numbers(num1, num2):
        '''
        Add two given numbers.
        arguments:
            num1 (number): first number
            num2 (number): second number
        return type: (number)
        '''
        return num1 + num2

    def subtract_numbers(num1, num2):
        '''
        Substract two given numbers.
        arguments:
            num1 (number): first number
            num2 (number): second number
        return type: (number)
        '''
        return num1 - num2

    def multiply_numbers(num1, num2):
        '''
        Multiply two given numbers.
        arguments:
            num1 (number): first number
            num2 (number): second number
        return type: (number)
        '''
        return num1 * num2

    def divide_numbers(num1, num2):
        return num1 / num2


