def get_code_39_font_data():
    """Returns a BytesIO file object containing the loaded normal Code 39 font. Takes no arguments."""
    with zipfile.ZipFile(io.BytesIO(FONT_DATA), "r") as z:
        with z.open("font.ttf", "r") as f:
            return io.BytesIO(f.read())


def get_code_39_extended_font_data():
    """Returns a BytesIO file object containing the loaded extended Code 39 font. Takes no arguments."""
    with zipfile.ZipFile(io.BytesIO(FONT_DATA), "r") as z:
        with z.open("font_ext.ttf", "r") as f:
            return io.BytesIO(f.read())


def get_code_39_font_and_extended_font_data():
    """Returns a tuple of BytesIO file objects containing the normal and extended fonts. Takes no arguments."""
    with zipfile.ZipFile(io.BytesIO(FONT_DATA), "r") as z:
        with z.open("font.ttf", "r") as f:
            normal = io.BytesIO(f.read())
        with z.open("font_ext.ttf", "r") as f:
            extended = io.BytesIO(f.read())
    return normal, extended


def encode_39(val: str):
    """Makes a string ready for the Code 39 font, returns a String."""
    return f'*{val.replace(" ", "_")}*'


class Encoder:
    """Encoder object to set overridable defaults and hold loaded font data. Setting a value in a method will temporarily overide the default."""

    def __init__(
        self,
        default_size: int = DEFAULT_SIZE,
        default_padx: int = DEFAULT_PAD,
        default_pady: int = DEFAULT_PAD,
        default_background: tuple = BACKGROUND,
        default_foreground: tuple = FOREGROUND,
    ):
        self.size, self.padx, self.pady = default_size, default_padx, default_pady
        self.background, self.foreground = default_background, default_foreground
        self.font = get_code_39_font_data()
        self.ext_font = get_code_39_extended_font_data()

    def make_barcode(
        self,
        val: str,
        size: int = None,
        padx: int = None,
        pady: int = None,
        background: tuple = None,
        foreground: tuple = None,
    ):
        """Make normal barcode, returns a PIL Image object."""
        self.font.seek(0)
        return self._make_barcode(
            val, self.font, size, padx, pady, background, foreground
        )

    def make_extended_barcode(
        self,
        val: str,
        size: int = None,
        padx: int = None,
        pady: int = None,
        background: tuple = None,
        foreground: tuple = None,
    ):
        """Make extended barcode, returns a PIL Image object."""
        self.ext_font.seek(0)
        return self._make_barcode(
            val, self.ext_font, size, padx, pady, background, foreground
        )

    def _make_barcode(
        self,
        val: str,
        font: io.BytesIO,
        size: int = None,
        padx: int = None,
        pady: int = None,
        background: tuple = None,
        foreground: tuple = None,
    ):
        """Core encoder barcode method, returns a PIL Image object."""
        return make_barcode(
            val,
            font,
            size or self.size,
            padx or self.padx,
            pady or self.pady,
            background or self.background,
            foreground or self.foreground,
        )


def make_barcode(
    val: str,
    font: io.BytesIO,
    size: int = DEFAULT_SIZE,
    padx: int = DEFAULT_PAD,
    pady: int = DEFAULT_PAD,
    background: tuple = BACKGROUND,
    foreground: tuple = FOREGROUND,
):
    """Core barcode generation function, returns a PIL Image object."""
    val = encode_39(val)
    loadedfont = ImageFont.truetype(font, size)
    l, t, r, b = loadedfont.getbbox(val)
    img = Image.new("RGBA", (r - l + 2 * padx, b - t + 2 * pady), tuple(background))
    ImageDraw.Draw(img).text(
        (padx, pady),
        val,
        fill=tuple(foreground),
        font=loadedfont,
        # anchor="lt",
    )
    return img


