from dash import html
import dash_bootstrap_components as dbc

def createcontent(numberofbanks=0,designation:str = 'data_management_app_'):

    layout = html.Div(className='container-fluid', children=[
        # this should now be contained in a rats card group...
        html.Div(className='card-group rats-card-group', children=[

            html.Div(className='card rats-card', children=[
                html.Div(className='card-header rats-card-header', children=[
                    html.Div(
                        [html.Button(id=f'{designation}pulldata', children='Click to scan for currently archived data',
                                     className='btn rats-btn', type='button', n_clicks=0)
                         ], id=f'{designation}pullcontainer', className='col-12 text-center')
                ]),

                html.Div(className='card-body text-center rats-card-body program-data', children=[

                    dbc.Checklist(
                        options=[],
                        id=f"{designation}data",
                        className='program-data-table'),

                    html.Div(id=f'{designation}report', className='data-use-report'),

                    dbc.Button(id=f'{designation}loaddata', children=['Load selected dataframes'], n_clicks=0,
                               className='btn btn-success rats-btn-success me-1', type='button'),

                    dbc.Button(id=f'{designation}delete', children='Delete selected dataframes',
                               className='btn btn-warning rats-btn-warning me-1', type='button', n_clicks=0),

                    dbc.Button(id=f'{designation}cleardata', children=['Delete all dataframes'], n_clicks=0,
                               className='btn btn-danger rats-btn-danger me-1', type='button'),

                    html.Div(className='data-management-bottom-padding'),
                ])
            ]),

            html.Div(className='card rats-card', children=[
                html.Div(className='card-header rats-card-header', children=[
                    html.P('Examine the log information generated by the parser:'),
                    dbc.Accordion(
                        [
                            dbc.AccordionItem(
                                [
                                    dbc.RadioItems(id=f'{designation}log-fileselect',
                                                   options=[],
                                                   value=[]
                                                   ),
                                ],
                                id=f'{designation}fileselect-accordion',
                                title="Select Data",
                            ),
                        ],
                        className=f'fileselect-accordion',
                        start_collapsed=True)
                ]),
                html.Div(className='card-body rats-card-body', children=[

                ], id=f'{designation}parser_log')
            ])



        ]),





    ])

    return layout
